import { NextResponse } from "next/server";
import { getTenant } from "@/entities/tenant";

export async function GET() {
  try {
    const tenant = getTenant();
    const subscriberId = tenant.subscriberId; // Ensure tenant is initialized
    // Sign the subscribe request ID using tenant's signing key
    const signedContent = await tenant.signSubscribeRequestId();

    // HTML template with signed content
    const htmlContent = `<!DOCTYPE html>
<html>
  <head>
    <meta
      name="ondc-site-verification"
      content="${signedContent}"
    />
  </head>
  <body style="font-family: Arial, sans-serif; text-align: center;">
    <div style="position:relative; height:100vh;">
      <div style="margin:auto;position: absolute;top: 50%;left: 50%;transform: translate(-50%, -50%);" >
        <h4>Hey there you gorgeous human being (or bot)!</h4>
        <div>
        
        <img style="height:200px;" src="data:image/svg+xml;base64," class="ImagePreviewModal__Illustration-sc-1umc9xg-3 jGgAzS"></img>
        </div>
        <p style="margin-top: 100px;">
        subscriber id: ${subscriberId}
        </p>
      </div>
    </div>
  </body>
</html>`;

    return new NextResponse(htmlContent, {
      status: 200,
      headers: {
        "Content-Type": "text/html",
      },
    });
  } catch (error) {
    console.error("[ondc-site-verification] Error:", error);
    return NextResponse.json(
      { error: "Internal server error" },
      { status: 500 },
    );
  }
}
